var MongoRepository = require('mongorepository').Repository;

var AuthorisedUserViewStore = function(){
	var viewStore = process.env.VIEW_STORE_CONNECTION_STRING;
	var authorisedUserRepository = new MongoRepository(viewStore,'authorised-users');

	function find(query,callback){
		authorisedUserRepository.find(query,function(results){
			callback(results);
		});
	}

	return{
		find: find
	};

};

module.exports = AuthorisedUserViewStore;

