var MongoRepository = require('mongorepository').Repository;

var CompanyDetailsViewStore = function(){
        var companyDetailsRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING,'company-details');
        
        function find(ASN,callback){
                var query = {_id:ASN};
                companyDetailsRepository.find(query, function(company){
                        callback(company);
                });
        }
        
        return{
                find: find
        };
};

module.exports = CompanyDetailsViewStore;