var MongoRepository = require('mongorepository').Repository;

var PendingMsaDataStore = function() {
		var pendingMsaRepository = new MongoRepository(process.env.DATA_STORE_CONNECTION_STRING, 'pending-msa');

		function get(ASN, callback) {
			pendingMsaRepository.find({
				ASN: ASN
			}, function(results) {
				callback(results);
			});
		}

		return{
			get:get
		};

	};

module.exports = PendingMsaDataStore;