var MongoRepository = require('mongorepository').Repository;

var PendingUsersViewStore = function(){
	var pendingUsersRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING,'pending-users');
	
	function find(ASN,callback){
		pendingUsersRepository.find({ASN:ASN},callback);
	}
	return{
		find: find
	};
};

module.exports = PendingUsersViewStore;