var MongoRepository = require('mongorepository').Repository;

var QuoteViewStore = function(){
	
	function get(quoteId, callback){
		var query = {"_id": quoteId};
		var quoteRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING,'quotes');
		quoteRepository.find(query, function(results){
				callback(results[0]);
			});
	}

	return {
		get:get
	};
};

module.exports = QuoteViewStore;