var MongoRepository = require('mongorepository').Repository;

var SignedMsaDataStore = function() {
		var signedMsaRepository = new MongoRepository(process.env.DATA_STORE_CONNECTION_STRING, 'signed-msa');

		function get(ASN, callback) {
			signedMsaRepository.find({
				ASN: ASN
			}, function(results) {
				callback(results);
			});
		}

		return {
			get: get
		};

	};

module.exports = SignedMsaDataStore;