var MongoRepository = require('mongorepository').Repository;

var TransitDataRateStore = function(){
        connectionString = process.env.VIEW_STORE_CONNECTION_STRING;
        var dataRatesForTransitStore = new MongoRepository(connectionString,'data-rates-transit');
        
        function getAll(callback){
                query = {}
                dataRatesForTransitStore.find(query, function(results){
                        console.log(results)
                        callback(results);
                });
        }

        return {
                getAll:getAll
        };
};

module.exports = TransitDataRateStore;