var MongoRepository = require('mongorepository').Repository;

var TransitDataRatesStore = function(){
	connectionString = process.env.VIEW_STORE_CONNECTION_STRING;
	var repo = new MongoRepository(connectionString,'data-rates-transit');
	
	function getAll(callback){
		repo.find({}, function(results){
			callback(results);
		});
	}

	return {
		getAll:getAll
	};
};

module.exports = TransitDataRatesStore;