var MongoRepository = require('mongorepository').Repository;

var TransitProvidersStoreViewStore = function(){
	var providerRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING,'transit-providers');
	function all(callback){
		var query = {};
		providerRepository.find(query, function(providers){
				callback(providers);
			});
	}

	return {
		all:all
	};
};

module.exports = TransitProvidersStoreViewStore;