var MongoRepository = require('mongorepository').Repository,
	transitVlanTagsViewRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING, 'transit-vlantags');

var TransitVlanTagsViewStore = function() {

		var findAvailable = function(providerName, callback) {

				transitVlanTagsViewRepository.find({
					ProviderName: providerName,
					ASN: ""
				}, function(vlanTags) {
					callback(vlanTags);
				});

			};

		return {
			findAvailable: findAvailable
		};
	};


module.exports = TransitVlanTagsViewStore;