var MongoRepository = require('mongorepository').Repository;	

var AuthorisedUsersStore = function(){
	var authorisedUsersRepository = new MongoRepository(process.env.VIEW_STORE_CONNECTION_STRING,'authorised-users');

	function all(callback){
		authorisedUsersRepository.find({},function(users){
			callback(users);
		});
	}

	return{
		all: all
	};
};

module.exports = AuthorisedUsersStore;