var MongoRepository = require('mongorepository').Repository;
var _ = require('underscore'),
	routeRepository = new MongoRepository(process.env.DATA_STORE_CONNECTION_STRING,'devices');


var DeviceStore = function(){
	
	var all = function(callback){
		var query = {};
		routeRepository.find(query, callback);
	};

	var find = function(deviceName, callback){
		var query = {"name": deviceName};
		routeRepository.find(query, callback);
	};
				
	return{
		all:all,
		find:find
	};
};


module.exports = DeviceStore;